(45, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'paypal-carding-recaptcha-dec-25', 'update', 'en_US', 'Activate PayPal fraud management', 'PayPal detected increased suspicious card activity in market. Please enable fraud protection in your PayPal Payment settings by enabling CAPTCHA for PayPal Payments.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'Googleupdate_q4_2025', 'marketing', 'en_US', 'Make every ad dollar count this holiday season', 'Google for WooCommerce now includes Budget Recommendations. Install the latest version and get personalized suggestions on where to optimize campaign budgets to reach more holiday shoppers this season.', '{}', 'pending', 'woocommerce.com', '2025-12-07 07:32:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2025-12-14 04:53:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2025-12-14 04:53:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-12-14 04:53:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'holiday_shipping_deadlines_WCST_2025', 'marketing', 'en_US', 'Holiday shipping deadlines', 'Ensure your packages arrive before December 25. Verify your exact ship-by date for your destination, carrier, and mail class.', '{}', 'pending', 'woocommerce.com', '2025-12-14 04:53:58', NULL, 0, 'plain', '', 0, 0, 'info'),
